import React, { Component } from 'react';
let request = require('request');
let link="https://images-assets.nasa.gov/image/PIA12235/PIA12235~thumb.jpg"
class SearchBar extends Component{
    constructor(props) {
        super(props);
        this.state = {value: '',searchImage:"https://images-assets.nasa.gov/image/PIA12235/PIA12235~thumb.jpg"};

        this.handleChange = this.handleChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
        // this.handleRefresh = this.handleRefresh.bind(this);
      }


      customerInput = React.createRef()


      handleChange(event) {
        this.setState({value: event.target.value})




      }


    handleSearch = async(event) =>{
        this.handleRefresh()

        event.preventDefault()
        var a = request.get(`https://images-api.nasa.gov/search?q=${this.state.value}&media_type=image`, function (error, response, body) {
        console.log('error:', error); 
        console.log('statusCode:', response && response.statusCode); 

        body= JSON.parse(body);

        link= body.collection.items[0].links[0].href 
        console.log(link); 
        a.then(function(){
            console.log("The Link is: "+this.state.searchImage)
            this.setState(prevState =>{
                return{ searchImage: prevState.searchImage=link}
            } )


        })
        // Print the HTML for the Google homepage.
      }) 
    }   














    render(){

        return (
            <div>

            <form onSubmit={this.handleSearch}>
            <label>  <input type="text"  value={this.state.value} onChange={this.handleChange} />  </label>
            <input type="submit" value="Submit" />

            </form>
            <img src = {this.state.searchImage} alt="N/A"></img>


            </div>)}}


export default SearchBar
