import React, { Component } from 'react';
let request = require('request');
let customerInput
class SearchBar extends Component{
    state={
        searchTerm: "Sun"
      }

      customerInput = React.createRef()

    handleSearch = event =>{
        event.preventDefault()
        request.get(`https://images-api.nasa.gov/search?q=${customerInput}&media_type=image`, function (error, response, body) {
        console.log('error:', error); 
        console.log('statusCode:', response && response.statusCode); 
        console.log(customerInput)
        body= JSON.parse(body);
        console.log(body.collection.items[0].links[0].href); // Print the HTML for the Google homepage.
      })}






    render(){

        return (
            <div>
            <form onSubmit={this.handleSearch}>
            <input type="text" 
                        ref ={this.customerInput} 
                        placeholder="Moon" 
                        />  
            <button onClick ={this.handleSearch}>Search</button> </form>



            </div>)}}


export default SearchBar
