import React, { Component } from 'react';
import './App.css';
import SearchBar from './SearchBar'

class App extends Component {
  state={
    searchTerm: "Moon"
  }

  handleSearch =() =>{
    var searchTerm = prompt("What to search on NASA", "Moon");
    this.setState(prevState => ({
    searchTerm: prevState.searchTerm = searchTerm
    })

    )}
  render() {
    return (
      <div className="App">

        <h1> Nasa Image search</h1>
        <img src="https://images-assets.nasa.gov/image/PIA12235/PIA12235~thumb.jpg"></img>

        <SearchBar />
      </div>

    )
  }}

export default App;
