import React, { Component } from 'react';
let request = require('request');
let a
class SearchBar extends Component{
    constructor(props) {
        super(props);
        this.state = {value: '',searchImage:""};

        this.handleChange = this.handleChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
      }

      customerInput = React.createRef()


      handleChange(event) {
        this.setState({value: event.target.value})

      }


    handleSearch = (event) =>{
        console.log(this.state.value)

        request.get(`https://images-api.nasa.gov/search?q=${this.state.value}&media_type=image`, function (error, response, body) {
        console.log('error:', error); 
        console.log('statusCode:', response && response.statusCode); 

        body= JSON.parse(body);

        a = body.collection.items[0].links[0].href 
        document.getElementById("image").innerHTML = <img src= {a} ></img>

        // Print the HTML for the Google homepage.
      }) 
      this.setState({searchImage: a})
      console.log(this.state.searchImage)
      event.preventDefault()}






    render(){

        return (
            <div>
            <form onSubmit={this.handleSearch}>
            <label>  <input type="text"  value={this.state.value} onChange={this.handleChange} />  </label>
            <input type="submit" value="Submit" />
            </form>
            <div id ='image'>

            </div></div>)}}


export default SearchBar
