import React, { Component } from 'react';
let request = require('request');
let customerInput
class SearchBar extends Component{
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
      }

      customerInput = React.createRef()


      handleChange(event) {
        this.setState({value: event.target.value});
      }


    handleSearch = event =>{
        event.preventDefault()
        request.get(`https://images-api.nasa.gov/search?q=${customerInput}&media_type=image`, function (error, response, body) {
        console.log('error:', error); 
        console.log('statusCode:', response && response.statusCode); 
        console.log(this.state)
        body= JSON.parse(body);
        console.log(body.collection.items[0].links[0].href); // Print the HTML for the Google homepage.
      })}






    render(){

        return (
            <div>
            <form onSubmit={this.handleSearch}>
            <label>  <input type="text"  value={this.state.value} onChange={this.handleChange} />  </label>
            <input type="submit" value="Submit" />
            </form>



            </div>)}}


export default SearchBar
